import React from 'react';
import './_Form.scss';
import FormHead from './FormHead';

class Form extends React.Component {
    state = {
        firstName: "",
        lastName: "",
        email: "",
        firstNameError: "",
        lastNameError: "",
        emailError: ""
    }

    handleChange = (event) => {
        const isCheckbox = event.target.type === 'checkbox';
        this.setState({
            [event.target.name]: isCheckbox ? event.target.checked : event.target.value
        });
    };

    validate = () => {
        // let firstNameError = "";
        // let lastNameError = "";
        let emailError = "";

        if (!this.state.email.includes('@')) {
            emailError = 'Invalid Email Address';
        }

        if (emailError) {
            this.setState({emailError});
            return false;
        }

        return true;
    }

    handleSubmit = (event) => {
        event.preventDefault();
        const isValid = this.validate();
        console.log(this.state);
    }


    render () {
        return (
            <>
            <FormHead />
            <div className="contact">
                <h2 className="contact__title">Newsletter</h2>
                <p className="contact__description">Recieve notifications when we post new albums</p>
            </div>

            <div className="form__wrapper">
                <form className="form" onSubmit={this.handleSubmit}>
                    <h1 className="form__title">Subscribe</h1>

                    <label htmlFor="inputFirstName"></label>
                    <input 
                        name="name" 
                        placeholder="First Name" 
                        value={this.state.firstName} 
                        onChange={this.handleChange} 
                        id="inputFirstName" 
                        className="form--control">
                    </input>
                    <div className="error">{this.state.firstNameError}</div>

                    <label htmlFor="inputLastName"></label>
                    <input 
                        name="name" 
                        placeholder="Last Name" 
                        value={this.state.firstName} 
                        onChange={this.handleChange} 
                        id="inputLastName" 
                        className="form--control">
                    </input>
                    <div className="error">{this.state.lastNameError}</div>

                    <label htmlFor="inputEmail"></label>
                    <input 
                        name="name" 
                        placeholder="Email Address" 
                        value={this.state.firstName} 
                        onChange={this.handleChange} 
                        id="inputEmail" 
                        className="form--control">
                    </input>
                    <div className="error">{this.state.emailError}</div>



                    <textarea id="inputMessage" className="form--control" placeholder="Leave us a message!"></textarea>

                    <button className="form--button" id="submitButton" type="submit" onClick={this.handleSubmitClick}>Submit</button>

                    <p className="form__copyright">© Soundclout.io</p>
                </form>
            </div>
            </>
        )
    }
}

export default Form;
