import React from 'react';
import './_Form.scss';
import FormHead from './FormHead';
import SimpleReactValidator from 'simple-react-validator';

class Form extends React.Component {
    constructor() {
        super();
        this.validator = new SimpleReactValidator();
        this.validator.message('title', this.state.title, 'required|email');

        this.state = {
            firstName: '',
            lastName: '',
            email: '',
        }
    }

    handleSubmitClick = (e) => {
        const firstName = document.querySelector('#inputFirstName');
        const lastName = document.querySelector('#inputLastName');
        const email = document.querySelector('#inputEmail');
        const message = document.querySelector('#inputMessage');
        let empty = "";

        if(firstName.value === '') {

        }

        alert(`Thanks for joining our community, ${firstName.value}!`);
        e.preventDefault();
        firstName.value = empty;
        lastName.value = empty;
        email.value = empty;
        message.value = empty;
    }

    render () {
        return (
            <>
            <FormHead />
            <div className="contact">
                <h2 className="contact__title">Newsletter</h2>
                <p className="contact__description">Recieve notifications when we post new albums</p>
            </div>

            <div className="form__wrapper">
                <form className="form" >
                    <h1 className="form__title">Subscribe</h1>

                    <label htmlFor="inputFirstName"></label>
                    <input type="text" id="inputFirstName" className="form--control" value={this.state.firstName} onChange={this.setFirstNAme}></input>

                    <label htmlFor="inputLastName"></label>
                    <input type="text" id="inputLastName" className="form--control" value={this.state.lastName}></input>

                    <label htmlFor="inputEmail"></label>
                    <input type="email" id="inputEmail" className="form--control" value={this.state.email}></input>

                    <textarea id="inputMessage" className="form--control" placeholder="Leave us a message!"></textarea>

                    <button className="form--button" id="submitButton" type="submit" onClick={this.handleSubmitClick}>Submit</button>

                    <p className="form__copyright">© Soundclout.io</p>
                </form>
            </div>
            </>
        )
    }
}

export default Form;
