import React from 'react';
import './_Form.scss';
import FormHead from './FormHead';

class Form extends React.Component {
    constructor() {
        super();
        this.state = {
          firstName: '',
          lastName: '',
          email: '',
          message: '',
        };
    }

    _handleFirstNameChange = (event) => {
        this.setState({ name: event.target.value });
    };

    _handleLastNameChange = (event) => {
        this.setState({ name: event.target.value });
    };

    _handleEmailChange = (event) => {
        this.setState({ name: event.target.value });
    };

    _handleMessageChange = (event) => {
        this.setState({ name: event.target.value });
    };

    _handleSubmitClick = (e) => {
        let firstName = this._firstName.value;
        let lastName = this._lastName.value;
        let email = this._email.value;
        let message = document.querySelector('#inputMessage');
        const empty = '';
        // do something with `name`

        alert(`Thanks for joining our community, ${firstName.value}!`);
        e.preventDefault();
        firstName.value = empty;
        lastName.value = empty;
        email.value = empty;
        message.value = empty;
    }

    render () {
        return (
            <>
            <FormHead />
            <div className="contact">
                <h2 className="contact__title">Newsletter</h2>
                <p className="contact__description">Recieve notifications when we post new albums</p>
            </div>

            <div className="form__wrapper">
                <form className="form">
                    <h1 className="form__title">Subscribe</h1>
                    <label htmlFor="inputFirstName"></label>
                    <input type="text" id="inputFirstName" className="form--control" placeholder="First Name" value={this.state.firstName} onChange={this._handleFirstNameChange}></input>
                    <label htmlFor="inputLastName"></label>
                    <input type="text" id="inputLastName" className="form--control" placeholder="Last Name" ref={input => this._lastName = input}></input>
                    <label htmlFor="inputEmail"></label>
                    <input type="email" id="inputEmail" className="form--control" placeholder="Email Address" ref={input => this._email = input}></input>
                    <textarea id="inputMessage" className="form--control" placeholder="Leave us a message!"></textarea>

                    <button className="form--button" id="submitButton" type="submit" onClick={this.handleSubmitClick}>Submit</button>

                    <p className="form__copyright">Â© Soundclout.io</p>
                </form>
            </div>
            </>
        )
    }
}

export default Form;
